import { useState } from "react";
import { nanoid } from "nanoid";
import { getDatabase, child, ref, set, get } from "firebase/database";
import { isWebUri } from "valid-url";
import OverlayTrigger from "react-bootstrap/OverlayTrigger";
import Tooltip from "react-bootstrap/Tooltip";

const Form = () => {
  const [formData, setFormData] = useState({
    longURL: "",
    generatedURL: "hi",
    loading: false,
    errors: [],
    errorMessages: {},
    toolTipMessage: "Copy To Clipboard",
  });

  const onSubmit = (e) => {
    e.preventDefault();
    setFormData({
      ...formData,
      loading: true,
      generatedURL: "",
    });

    // validate the user input
    var isFormValid = validateInput();
    if (!isFormValid) {
      return;
    }

    var generatedKey = nanoid(5);
    var generatedURL = "smallurl.com/" + generatedKey;

    const db = getDatabase();
    set(ref(db, "/" + generatedKey), {
      generatedKey: generatedKey,
      longURL: formData.longURL,
      generatedURL: generatedURL,
    })
      .then((result) => {
        setFormData({
          ...formData,
          generatedURL: generatedURL,
          loading: false,
        });
      })
      .catch((e) => {
        //handle error
      });
  };

  //haserror
  //handle change

  const validateInput = () => {
    var errors = [];
    var errorMessages = this.state.errorMessage;

    //Validate Long URL
    if (formData.longURL.length === 0) {
      errors.push("longURL");
      errorMessages["longURL"] = "Please enter your URL!";
    } else if (!isWebUri(formData.longURL)) {
      errors.push("longURL");
      errorMessages["longURL"] = "Please a URL in the form of https://www....";
    }

    setFormData({
      ...formData,
      errors: errors,
      errorMessages: errorMessages,
      loading: false,
    });

    if (errors.length > 0) {
      return false;
    }
    return true;
  };

  const copyToClipBoard = () => {
    navigator.clipboard.writeText(this.state.generatedURL);
    this.setState({
      toolTipMessage: "Copied!",
    });
  };

  return (
    <div>
      <h1>Peter</h1>
      <button onClick={onSubmit}>Click me</button>
    </div>
  );
};

export default Form;
